mapper.select:
SELECT totalCount, totalPage, currentPage, if(currentPage=0, 1, 0) FIRST, if(currentPage=totalPage-1, 1, 0) LAST FROM( SELECT count(*) totalCount, ceil(count(*)/5) totalPage, #{page} currentPage, 0 first, 0 last FROM board
WHERE title like CONCAT('%', #{keyword}, '%')
) paging

mapper.update:
          UPDATE board
		  SET title = #{title},
		  thumbnail = #{thumbnail},
		  content = #{content},
		  category_id = #{categoryId}
		  WHERE board_id=#{boardId}
