mapper.select:
SELECT users_id, username, realname, nickname, email, address, phone, photo, state
FROM users 
WHERE users_id=#{usersId}

mapper.update:
UPDATE users
SET state = "inactive"
WHERE users_id=#{usersId}

UPDATE users
SET state = "stop"
WHERE users_id=#{usersId}

UPDATE users
SET state = "active"
WHERE users_id=#{usersId}
